<?php  session_start();    Include('conexao_free.php');  mysqli_set_charset($con,'UTF8');    function get_post_action($name) {    $params = func_get_args();    foreach ($params as $name) {        if (isset($_POST[$name])) {            return $name;        }    }  }?><HTML>  <HEAD>    <TITLE>altera_tabela_preco_cliente.php</TITLE>      <style>		 body, p, div, td, input, select, textarea {			font-family: verdana,arial,helvetica;			font-size:12px;			color:#000000;			text-decoration: none;		 }		 input,textarea {			@if (is.ie) {				color: #efefef; background-color:#F0F8FF; border: 1px solid #6495ED ;				/*border-top-width: 1px; border-right-width: 1px; border-bottom-width: 1px; border-left-width: 1px; */			}		 }		 textarea { overflow:auto }	  </style>	  	  <script language="JavaScript" type="text/JavaScript">          function Refresh() {             window.opener.location.href = "atualiza_tabela_preco_cliente.php";          }          function MM_openBrWindow(theURL,winName,features) {             window.open(theURL,winName,features);          }                    function CloseWindow() {             Refresh();             window.close();          }      </script>   </HEAD>   <body leftmargin="10" topmargin="10" marginwidth="10" marginheight="10">   <form name="cadastro" action="altera_tabela_preco_cliente.php" method="post">   <INPUT type=button value="Fechar janela" onClick="javascript:CloseWindow();">   <?php    switch (get_post_action('grava')) {         case 'grava':             $controle       =$_SESSION['controle_m'];             $servico        = $_POST['tipo_servi'];             $preco          = $_POST['valor'];                          $alteracao = "UPDATE tabela_preco SET tipo_servi='$servico',valor='$preco'             WHERE controle='$controle'";             mysqli_query($con,$alteracao);             $controle       ='';             unset($_SESSION['controle_m']);             $cliente        ='';             $classe         ='';             $nome_classe    ='';             $valor          ='';             $tipo_servi     ='';             ?>             <script>                CloseWindow();             </script>             <?php         break;         default:    }    $controle                =$_GET['controle'];    $_SESSION['controle_m']  =$controle;    $resp_grava='';        $verifi="SELECT serv_ati.descri_se,tabela_preco.classe_cep,classe_cep.descricao,    tabela_preco.valor,tabela_preco.tipo_servi,cli_for.nome    FROM tabela_preco,serv_ati,cli_for,classe_cep    WHERE ((tabela_preco.controle='$controle')    AND (serv_ati.codigo_se=tabela_preco.tipo_servi)    AND (tabela_preco.codi_cli=cli_for.cnpj_cpf)    AND (tabela_preco.classe_cep=classe_cep.codigo))";    $query = mysqli_query($con,$verifi) or die ("Não foi possivel acessar o banco");    $total = mysqli_num_rows($query);    for($ic=0; $ic<$total; $ic++){          $mostra = mysqli_fetch_row($query);          $servico     = $mostra[0];          $classe      = $mostra[1];          $nome_classe = $mostra[2];          $valor       = $mostra[3];          $tipo_servi  = $mostra[4];          $cliente     = $mostra[5];    }    ?>	<table width="100%" border="1" cellpadding="5" cellspacing="0" bordercolor="#4169E1">       <tr>           <td><b>Cliente :</b></td>           <td>              <font size="2" color="red"><?php echo "$cliente";?></font>            </td>		</tr>        <tr>		  <td><b>Serviço :</b></td>           <td>			  <?php               //$tipo_servi     =$_SESSION['tipo_servi_m'];               echo "<select name=\"tipo_servi\">";               $sql3 = "SELECT codigo_se,descri_se FROM serv_ati";               $resulo = mysqli_query($con,$sql3) or die ("Não foi possivel acessar o banco");               while ( $linha = mysqli_fetch_array($resulo)) {                    $select = $tipo_servi == $linha[0] ? "selected" : "";                    echo "<option value=\"". $linha[0] . "\" $select>" . $linha[1] . "</option>";               }              ?>              </select>           </td>		</tr>         <tr>		  <td><b>Classe CEP :</b></td>            <td>              <font size="2" color="red"><?php echo "$classe";?></font>            </td>		</tr>		</tr>         <tr>		  <td><b>Nome Classe CEP :</b></td>            <td>              <font size="2" color="red"><?php echo "$nome_classe";?></font>            </td>		</tr>		<tr>			<td><b>Valor :</b></td>			<td><input name="valor" type="text" value ="<?php echo "$valor";?>" size="20" maxlength="20" onKeydown="Formata(this,20,event,2)"></td>		</tr>		<tr>			<td colspan="2">				<div align="right">				<input name="grava" type="submit" value="Gravar">				</div>			</td>		</tr>	</table>  </form>  </div>  <table width="100%" border="0" cellspacing="0" cellpadding="0">      <tr>        <td color="white" align="center" width="900" height="45" colspan="8" ><?php echo "$resp_grava";?></td>      </tr>  </table></body></html>