  $nome_desti;               $_SESSION['comple_m']        =  $comple;               $_SESSION['telefone_m']      =  $telefone;               $_SESSION['dt_atu_cada_m']   =  $dt_atu_cada;               $_SESSION['classe_cep_m']    =  $classe_cep;               $_SESSION['rede_m']          =  $rede;             }                             break;			          case 'mostra':             $codigo_desti      =$_POST['codigo_desti'];                          $mostra="SELECT codigo_desti,nome_desti,cep_desti,rua_desti,numero_desti,             comple_desti,bairro_desti,cidade_desti,estado_desti,fone_desti,             date_format(dt_atu_cada,'%d/%m/%Y'),classe_cep,rede             FROM destino WHERE trim(codigo_desti)='$codigo_desti'";			              $query = mysqli_query($con,$mostra) or die ("Não foi possivel acessar a tabela DESTINOS");             $total = mysqli_num_rows($query);			              for($ic=0; $ic<$total; $ic++){               $mostra = mysqli_fetch_row($query);               $codigo_desti   = $mostra[0];               $nome_desti     = $mostra[1];               $cep            = $mostra[2];               $rua            = $mostra[3];               $numero         = $mostra[4];               $comple         = $mostra[5];               $bairro         = $mostra[6];               $cidade         = $mostra[7];               $estado         = $mostra[8];               $telefone       = $mostra[9];               $dt_atu_cada    = $mostra[10];               $classe_cep     = $mostra[11];               $rede           = $mostra[12];             }             break;			          case 'busca':           $cep  =$_POST['cep'];		              //Busca o endereco a partir do cep digitado		              $busca="SELECT logradouros.no_logradouro_cep,logradouros.ds_logradouro_nome,		   bairros.ds_bairro_nome,cidades.ds_cidade_nome,uf.ds_uf_nome,uf.ds_uf_sigla,logradouros.classe_cep		   FROM logradouros,bairros,cidades,uf		   WHERE ((logradouros.cd_bairro=bairros.cd_bairro)		   AND (bairros.cd_cidade=cidades.cd_cidade)		   AND (uf.cd_uf=cidades.cd_uf)		   AND (logradouros.no_logradouro_cep='$cep'))";		   $resu = mysqli_query($con,$busca) or die ("Não foi possivel acessar o banco - Função Busca Cep");		   $total_c = mysqli_num_rows($resu);           		   if($total_c > 0) {			   for($ic=0; $ic<$total_c; $ic++){				   $row = mysqli_fetch_row($resu);				   				   $cep            = $row[0];				   $rua            = $row[1];				   $bairro         = $row[2];				   $cidade         = $row[3];				   $estado_n       = $row[4];				   $estado         = $row[5];				   $classe_cep     = $row[6];  			   }			   			   //recupera variaceis colocadas na memoria porque pesquisa limpou campos preenchidos			   			   $codigo_desti   = $_SESSION['codigo_desti_m'];               $nome_desti     = $_SESSION['nome_desti_m'];               $comple         = $_SESSION['comple_m'];               $telefone       = $_SESSION['telefone_m'];               $dt_atu_cada    = $_SESSION['dt_atu_cada_m'];               $classe_cep     = $_SESSION['classe_cep_m'];               $rede           = $_SESSION['rede_m'];		   }           else {		      			  $resp_grava='CEP não localizado! Verifique.';		   }            break;		          case 'grava':             $codigo_desti    =$_POST['codigo_desti'];             $nome_desti      =$_POST['nome_desti'];             $rua             =$_POST['rua'];             $numero          =$_POST['numero'];			 $comple          =$_POST['comple'];             $bairro          =$_POST['bairro'];             $cidade    	  =$_POST['cidade'];             $estado          =$_POST['estado'];             $cep             =$_POST['cep'];             $telefone        =$_POST['telefone'];             $dt_atu_cada     =$_POST['dt_atu_cada'];             $classe_cep      =$_POST['classe_cep'];             $rede            =$_POST['rede'];             $nome_desti      =Strtoupper($nome_desti);             $rua             =Strtoupper($rua);             $bairro          =Strtoupper($bairro);             $cidade          =Strtoupper($cidade);             $estado          =Strtoupper($estado);                          //mudando formato da data para gravar na tabela             $dt_atu_cada  = explode("/",$dt_atu_cada);             $v_dt_atu_cada = $dt_atu_cada[2]."-".$dt_atu_cada[1]."-".$dt_atu_cada[0];			              $alteracao = "UPDATE destino SET nome_desti='$nome_desti',             rua_desti='$rua',numero_desti='$numero',comple_desti='$comple',             bairro_desti='$bairro',cidade_desti='$cidade',             estado_desti='$estado',cep_desti='$cep',fone_desti='$telefone',             dt_atu_cada='$v_dt_atu_cada',classe_cep='$classe_cep',rede='$rede'             WHERE codigo_desti='$codigo_desti'";			              if (mysqli_query($con,$alteracao)) {                $resp_grava="Alteração bem sucedida";             }             else {                $resp_grava="Problemas na Alteração";             }			 			 $codigo_desti    ='';			 $nome_desti      ='';			 $cep             ='';			 $rua             ='';			 $numero          ='';			 $comple          ='';			 $bairro          ='';			 $cidade          ='';			 $estado_n        ='';			 $estado          ='';			 $telefone        ='';			 $classe_cep      ='';			 $rede            =''; 			 $dt_atu_cada     ='';            break;         default:     }  ?>  <body leftmargin="10" topmargin="10" marginwidth="10" marginheight="10">  <form name="cadastro" action="cadastro_destinos_altera.php" method="post" onSubmit="return validaForm()">	<table width="50%" border="1" cellpadding="5" cellspacing="0" bordercolor="#4169E1">		<tr>			<td><b>Nome :</b></td>			<td><input type="text" name="nome_desti" value ="<?php echo "$nome_desti";?>" size="50" maxlength="50" id="nome_desti"class="input_forms"/><input name="localiza" type="submit" value="Busca