.href=("entrada.php")                 alert('Já existe departamento cadastrado com o codigo informado.');               </script>             <?php           }           else {             $inclusao = "INSERT INTO regi_dep(codigo,nome,gerente,             sigla,cep,rua,numero,comple,bairro,cidade,estado,ende_ele,ativo,telefone)             values('$codigo','$nome','$gerente','$sigla','$cep','$rua','$numero','$comple',             '$bairro','$cidade','$estado','$ende_ele','$ativo','$telefone')";             if (mysqli_query($con,$inclusao) or die ("Não foi possivel gravar na tabela Escritorio: ".mysqli_errno($con)." - ".mysqli_error($con))) {                $resp_grava="Inclusão bem sucedida";                 }             else {               $resp_grava="Problemas na Inclusão";             }           }            $codigo          ='';					$nome            ='';			$gerente         ='';					$sigla           ='';					$cep             ='';					$rua             ='';			$numero          ='';			$comple          ='';					$bairro          ='';					$cidade          ='';				$estado_n        ='';			$estado          ='';			$ativo           ='';			$ende_ele        ='';			$ramal           ='';        }       break;    }  ?>  <body leftmargin="10" topmargin="10" marginwidth="10" marginheight="10">  <table width="100%" border="0" cellspacing="0" cellpadding="0">      <tr>         <td color="white" align="left" width="100%" height="35">      </tr>    </table>  <form name="cadastro" id="cadastro" action="cadastro_escritorios.php" method="post">	<table width="50%" border="1" cellpadding="5" cellspacing="0" bordercolor="#6495ED">		<tr>			<td><b>Codigo :</b></td>			<td><input type="text" name="codigo" value ="<?php echo "$codigo";?>" size="15" maxlength="15" id="codigo"></td>		</tr>		<tr>			<td><b>Nome :</b></td>			<td><input type="text" name="nome" value ="<?php echo "$nome";?>" size="50" maxlength="50" id="nome"></td>		</tr>		<tr>			