  $_SESSION['ende_ele']    = $ende_ele;               $_SESSION['telefone']    = $telefone;               $_SESSION['ativo']       = $ativo;               $_SESSION['prazo_pod']   = $prazo_pod;           }      break;            case 'busca':           $cep       =$_POST['cep'];           $codigo    =$_POST['codigo'];           $nome      =$_POST['nome'];           $sigla     =$_POST['sigla'];                      //Busca o endereco a partir do cep digitado		              $busca="SELECT logradouros.no_logradouro_cep,logradouros.ds_logradouro_nome,		   bairros.ds_bairro_nome,cidades.ds_cidade_nome,uf.ds_uf_nome,uf.ds_uf_sigla,logradouros.classe_cep		   FROM logradouros,bairros,cidades,uf		   WHERE ((logradouros.cd_bairro=bairros.cd_bairro)		   AND (bairros.cd_cidade=cidades.cd_cidade)		   AND (uf.cd_uf=cidades.cd_uf)		   AND (logradouros.no_logradouro_cep='$cep'))";		   $resu = mysqli_query($con,$busca) or die ("Não foi possivel acessar o banco - Função Busca Cep");		   $total_c = mysqli_num_rows($resu);           		   if($total_c > 0) {			   for($ic=0; $ic<$total_c; $ic++){				   $row = mysqli_fetch_row($resu);				   				   $cep            = $row[0];				   $rua            = $row[1];				   $bairro         = $row[2];				   $cidade         = $row[3];				   $estado_n       = $row[4];				   $estado         = $row[5];				   $classe_cep     = $row[6];  			   }			   			   //recupera os dados da memoria que foram apagados do form ao recarrega-lo			                 $gerente   = $_SESSION['gerente'];               $numero    = $_SESSION['numero'];               $comple    = $_SESSION['comple'];               $ende_ele  = $_SESSION['ende_ele'];               $telefone  = $_SESSION['telefone'];               $ativo     = $_SESSION['ativo'];               $prazo_pod = $_SESSION['prazo_pod']; 		   }      break;            case 'grava':        if (isset($_POST['codigo'])) {           $codigo          =$_SESSION['codigo_m'];           $nome            =$_POST['nome'];           $gerente         =$_POST['gerente'];           $sigla           =$_POST['sigla'];           $cep             =$_POST['cep'];           $rua             =$_POST['rua'];           $numero          =$_POST['numero'];           $comple          =$_POST['comple'];           $bairro          =$_POST['bairro'];           $cidade          =$_POST['cidade'];           $estado          =$_POST['estado'];           $ativo           =$_POST['ativo'];           $ende_ele        =$_POST['ende_ele'];           $telefone        =$_POST['telefone'];           $prazo_pod       =$_POST['prazo_pod'];		              $sigla           =Strtoupper($sigla);           $ativo           =Strtoupper($ativo);                      $alteracao = "UPDATE regi_dep SET nome='$nome',gerente='$gerente',sigla='$sigla',           cep='$cep',rua='$rua',numero='$numero',comple='$comple',bairro='$bairro',           cidade='$cidade',estado='$estado',ende_ele='$ende_ele',ativo='$ativo',telefone='$telefone',           prazo_pod='$prazo_pod'           WHERE codigo='$codigo'";           if (mysqli_query($con,$alteracao) or die ("Não foi possivel alterar a tabela Escritorio: ".mysqli_errno($con)." - ".mysqli_error($con))) {                $resp_grava="Alteração bem sucedida";           }           else {             $resp_grava="Problemas na Alteração";           }            $codigo          ='';					$nome            ='';			$gerente         ='';					$sigla           ='';					$cep             ='';					$rua             ='';			$numero          ='';			$comple          ='';					$bairro          ='';					$cidade          ='';				$estado_n        ='';			$estado          ='';			$ativo           ='';			$ende_ele        ='';			$ramal           ='';			$resp_grava      ='';            $prazo_pod       ='';			        }       break;    }  ?>  <body leftmargin="10" topmargin="10" marginwidth="10" marginheight="10">  <table width