c, tam ) ; }          if ( (tam >= 12) && (tam <= 14) ){          campo.value = vr.substr( 0, tam - 11 ) + "." + vr.substr( tam - 11, 3 ) + "." + vr.substr( tam - 8, 3 ) + "." + vr.substr( tam - 5, 3 ) + "," + vr.substr( tam - dec, tam ) ; }          if ( (tam >= 15) && (tam <= 17) ){          campo.value = vr.substr( 0, tam - 14 ) + "." + vr.substr( tam - 14, 3 ) + "." + vr.substr( tam - 11, 3 ) + "." + vr.substr( tam - 8, 3 ) + "." + vr.substr( tam - 5, 3 ) + "," + vr.substr( tam - 2, tam ) ;}          }     }</script>  <style>		body, p, div, td, input, select, textarea {			font-family: verdana,arial,helvetica;			font-size:12px;			color:#000000;			text-decoration: none;		}		input,textarea {			@if (is.ie) {				color: #efefef; background-color:#F0F8FF; border: 1px solid #6495ED ;				/*border-top-width: 1px; border-right-width: 1px; border-bottom-width: 1px; border-left-width: 1px; */			}		}		textarea { overflow:auto }	</style>  </head>  <body leftmargin="10" topmargin="10" marginwidth="10" marginheight="10">  <table width="100%" border="0" cellspacing="0" cellpadding="0">      <tr>         <td color="white" align="left" width="100%" height="35">      </tr>    </table>  <form name="cadastro" id="cadastro" action="cadastro_tabela_preco_contratado.php" method="post">	<table width="70%" border="1" cellpadding="5" cellspacing="0" bordercolor="#4169E1">        <tr>           <td><b>Representante :</b></td>           <td>              <?php                $repre   =$_SESSION['repre_m'];                echo "<select name=\"repre\">";                $sql1 = "SELECT cnpj_cpf,nome,uf FROM cli_for WHERE catego='F'";                $resula = mysqli_query($con,$sql1) or die ("Não foi possivel acessar o banco");                while ( $linha = mysqli_fetch_array($resula)) {                    $select = $repre == $linha[0] ? "selected" : "";                    echo "<option value=\"". $linha[0] . "\" $select>" . $linha[1] ." - ". $linha[2] 